		//#endif
	},
	methods: {
		initPage: function(data) {
			var that = this;
			that.init(data);
		},
		init: function(array, callback) {
			var that = this;
			if (array.length <= 0) {
				that.wxaSortPickerData.textData = []
				return false;
			}
			var temData = that.wxaSortPickerData;
			if (typeof temData == 'undefined') {
				temData = {};
			}
			/* that.wxaSortPickerUpper = wxaSortPickerUpper;
				that.wxaSortPickerLower = wxaSortPickerLower;
				that.wxaSortPickerScroll = wxaSortPickerScroll;
				that.wxaSortPickerTemTagTap = wxaSortPickerTemTagTap; */

			that.setViewWH(that);
			that.buildTextData(that, array);
		},
		wxaSortPickerItemTap: function(e) {
			var that = this;
			var pIndex = e.target.dataset.pindex;
			var index = e.target.dataset.index;
			var textArray = this.wxaSortPickerData.textData[pIndex].textArray[index];			
			var data = textArray;
			that.$emit('clickData', data);
		},
		wxaSortPickerTemTagTap: function(e) {
			var that = this;
			var temData = that.wxaSortPickerData;
			temData.wxaSortPickertoView = e.target.dataset.tag;
			that.wxaSortPickerData = temData;
		},
		makePy: function(str) {
			var that = this;
			if (typeof str != 'string') throw new Error(-1, '函数makePy需要字符串类型参数!');
			var arrResult = new Array(); //保存中间结果的数组
			for (var i = 0, len = str.length; i < len; i++) {
				//获得unicode码
				var ch = str.charAt(i);
				//检查该unicode码是否在处理范围之内,在则返回该码对映汉字的拼音首字母,不在则调用其它函数处理
				arrResult.push(that.checkCh(ch));
			}
			//处理arrResult,返回所有可能的拼音首字母串数组
			return that.mkRslt(arrResult);
		},
		checkCh: function(ch) {
			var that = this;
			var uni = ch.charCodeAt(0);
			//如果不在汉字处理范围之内,返回原字符,也可以调用自己的处理函数
			if (uni > 40869 || uni < 19968) return ch; //dealWithOthers(ch);
			//检查是否是多音字,是按多音字处理,不是就直接在strChineseFirstPY字符串中找对应的首字母
			return that.oMultiDiff[uni] ? that.oMultiDiff[uni] : that.strChineseFirstPY.charAt(uni - 19968);
		},
		mkRslt: function(arr) {
			var arrRslt = [''];
			for (var i = 0, len = arr.length; i < len; i++) {
				var str = arr[i];
				var strlen = str.length;
				if (strlen == 1) {
					for (var k = 0; k < arrRslt.length; k++) {
						arrRslt[k] += str;
					}
				} else {
					var tmpArr = arrRslt.slice(0);
					arrRslt = [];
					for (k = 0; k < strlen; k++) {
						//复制一个相同的arrRslt
						var tmp = tmpArr.slice(0);
						//把当前字符str[k]添加到每个元素末尾
						for (var j = 0; j < tmp.length; j++) {
							tmp[j] += str.charAt(k);
						}
						//把复制并修改后的数组连接到arrRslt上
						arrRslt = arrRslt.concat(tmp);
					}
				}
			}
			return arrRslt;
		},
		//两端去空格函数
		trim: function(str) {
			return str&&str.replace(/(^\s*)|(\s*$)/g, '');
		},
		//
		getPosition: function(obj) {
			var top = 0;
			var left = 0;
			var width = obj.offsetWidth;
			var height = obj.offsetHeight;
			while (obj.offsetParent) {
				top += obj.offsetTop;
				left += obj.offsetLeft;
				obj = obj.offsetParent;
			}
			return { top: top, left: left, width: width, height: height };
		},
		query: function(text) {
			var that = this;
			var str = that.trim(text);
			if (!str) return;
			var arrRslt = that.makePy(str);
			return arrRslt;
		},
		buildTextData: function(that, arr) {
			if (typeof arr[0] === 'object') {
				that.dataType = 'object';
			}
			var textData = [
				{ tag: 'A', textArray: [] },
				{ tag: 'B', textArray: [] },
				{ tag: 'C', textArray: [] },
				{ tag: 'D', textArray: [] },
				{ tag: 'E', textArray: [] },
				{ tag: 'F', textArray: [] },
				{ tag: 'G', textArray: [] },
				{ tag: 'H', textArray: [] },
				{ tag: 'I', textArray: [] },
				{ tag: 'J', textArray: [] },
				{ tag: 'K', textArray: [] },
				{ tag: 'L', textArray: [] },
				{ tag: 'M', textArray: [] },
				{ tag: 'N', textArray: [] },
				{ tag: 'O', textArray: [] },
				{ tag: 'P', textArray: [] },
				{ tag: 'Q', textArray: [] },
				{ tag: 'R', textArray: [] },
				{ tag: 'S', textArray: [] },
				{ tag: 'T', textArray: [] },
				{ tag: 'U', textArray: [] },
				{ tag: 'V', textArray: [] },
				{ tag: 'W', textArray: [] },
				{ tag: 'X', textArray: [] },
				{ tag: 'Y', textArray: [] },
				{ tag: 'Z', textArray: [] },
				{ tag: '#', textArray: [] }
			];

			var temABC = that.charsABC;
			if (that.dataType == 'object') {
				for (var i = 0; i < arr.length; i++) {
					var text = arr[i];
					
					var firstChar = text.name&&text.name.substr(0, 1);
					firstChar = firstChar&&firstChar.toUpperCase();
					var reg = that.query(firstChar)&&that.query(firstChar)[0];
					var temIndex = temABC.indexOf(reg);
					if (temIndex == -1) {
						temIndex = 26;
					}
					textData[temIndex].textArray.push(text);
				}
			} else {
				for (var i = 0; i < arr.length; i++) {
					var text = arr[i];
					var firstChar = text&&text.substr(0, 1);
					var reg = that.query(firstChar)[0];
					var temIndex = temABC.indexOf(reg);
					textData[temIndex].textArray.push(text);
				}
			}

			var temData = that.wxaSortPickerData;
			if (typeof temData == 'undefined') {
				temData = {};
			}
			temData.textData = textData;
			that.wxaSortPickerData = temData;
		},
		wxaSortPickerUpper: function(e) {
			//console.dir(e);
		},
		wxaSortPickerLower: function(e) {
			//console.dir(e);
		},
		wxaSortPickerScroll: function(e) {
			//console.log(e.detail.scrollTop);
		},
		setViewWH: function(that) {
			var that = this;
			uni.getSystemInfo({
				success: function(res) {
					var windowHeight = res.windowHeight;
					var temData = that.wxaSortPickerData;
					if (typeof temData == 'undefined') {
						temData = {};
					}
					var view = {};
					that.scrollHeight = windowHeight; // windowHeight
					temData.view = view;
					that.wxaSortPickerData = temData;
				}
			});
		}
	}
};
